import React, { useState } from "react";
import Style from "../../assets/style/contact.css";

export default function Contact() {
  // Create state variables for the fields in the form
  // We are also setting their initial values to an empty string
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [message, setMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleInputChange = (e) => {
    const { target } = e;
    const inputType = target.name;
    const inputValue = target.value;

    if (inputType === 'email') {
      setEmail(inputValue);
    } else if (inputType === 'name') {
      setName(inputValue);
    } else {
      setMessage(inputValue);
    }
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    setName('');
    setMessage('');
    setEmail('');
  };

  return (
    <div className="content">
      <div className="cardish">
        <p>If you want to connect, you can fill this form or use the links down below! Thank you!</p>
        <form>
          <input
            value={name}
            className="form-input w-100"
            name="name"
            onChange={handleInputChange}
            type="text"
            placeholder="name"
          />
          <input
            value={email}
            className="form-input w-100"
            name="email"
            onChange={handleInputChange}
            type="email"
            placeholder="email"
          />
          <input
            value={message}
            className="form-input w-100"
            name="message"
            onChange={handleInputChange}
            type="message"
            placeholder="message"
          />
          <button className="btn btn-primary btn-block py-3" type="button" onClick={handleFormSubmit}>Submit</button>
        </form>
        {errorMessage && (
          <div>
            <p className="error-text">{errorMessage}</p>
          </div>
        )}
      </div>
    </div>
  );
}
